{
	"name": "test-rule-20211008-recreate-by-api",
	"active": true,
	"conditions": [
		{
			"name": "condition_set_1",
			"match_type": "all",
			"properties": [
				{
					"field_name": "cf_csp",
					"resource_type": "ticket",
					"operator": "in",
					"value": [
						"IBM"
					]
				},
				{
					"field_name": "cf_csp",
					"resource_type": "ticket",
					"operator": "in",
					"value": [
						"경기도 클라우드 지원사업"
					]
				}
			]
		}
	],
	"actions": [
		{
			"request_type": "POST",
			"url": "https://stg-ticket-ibm.cloudzcp.io/ibm/createTicket/{{ticket.id}}",
			"field_name": "trigger_webhook",
			"content_type": "JSON",
			"content_layout": "2",
			"content": {
				"public_url": "{{ticket.public_url}}"
			},
			"custom_headers": {
				"authorization": "5C2XkNLcGi4vKaOdVq0s"
			}
		},
		{
			"field_name": "cf_escalation",
			"value": "Y"
		},
		{
			"field_name": "add_note",
			"note_body": "<div dir=\"ltr\">규칙 테스트 노트입니다.</div>",
			"notify_agents": [
				2043067851925
			]
		}
	]
}

